function triggeerMenu(){const btnTrigger=document.querySelector(".trigger-menu"),menuBlock=document.querySelector(".header-nav"),header=document.querySelector(".header-wrap");btnTrigger.addEventListener("click",()=>{console.log(),"header-nav_disable"===menuBlock.classList[1]?(menuBlock.classList.remove("header-nav_disable"),header.style.cssText="height: 200px"):(menuBlock.classList.add("header-nav_disable"),header.style.cssText="height: 115px")})}function showCookie(){$(".cookie-popup").hide(),null==Cookies.get("showCookieDialog")&&(Cookies.set("showCookieDialog","yes",{expires:7}),$(".cookie-popup").show())}document.addEventListener("DOMContentLoaded",()=>{triggeerMenu(),showCookie()}),$(document).ready(function(){$(".cookie-popup__button").click(function(){$(".cookie-popup").hide()})});