function triggeerMenu(){const btnTrigger=document.querySelector(".trigger-menu"),menuBlock=document.querySelector(".header-nav"),header=document.querySelector(".header-wrap");btnTrigger.addEventListener("click",()=>{console.log(),"header-nav_disable"===menuBlock.classList[1]?(menuBlock.classList.remove("header-nav_disable"),header.style.cssText="height: 200px"):(menuBlock.classList.add("header-nav_disable"),header.style.cssText="height: 115px")})}document.addEventListener("DOMContentLoaded",()=>{triggeerMenu()});